// Settings
var dontTurnOnDevicesList = [
    "light.living_room_lamp_light"
    ];

// Variables
var devices = [
    "light.living_room_light_light",
    "light.living_room_lamp_light",
    "light.kitchen_light"
    ]//global.get("AllLights");
var response = [];
var turningOn = true;

// Check if turning lights on or off
for(let deviceName of devices) {
    var device = global.get("homeassistant").homeAssistant.states[deviceName];
    var currentState = device.attributes.state;
    if(currentState == "ON") {
        node.warn("Light <" + deviceName + "> is ON");
        turningOn = false;
        break;
    }
}
node.warn("Turning lights in living room <" + (turningOn ? "ON" : "OFF") + ">");

// Turn on/off devices
for(let deviceName of devices) {
    if(turningOn && dontTurnOnDevicesList.includes(deviceName)) {
       response.push(null);
       continue;
    }

    msg = {
        domain: deviceName.substr(0, deviceName.indexOf('.')), // light/switch
        service: "turn_" + (turningOn ? "on" : "off"),
        data: { entity_id: deviceName }
    };
    response.push({ payload: msg });
}

node.warn(response);
return response;

